import React from "react";
import "../../RecipeModal.css";  // Import the styles for RecipeModal

const RecipeModal = ({ img, title, description, closeModal }) => {
  return (
    <div className="recipe-modal">
      <div className="recipe-modal__content">
        <img src={img} alt={title} className="recipe-modal__image" />
        <h2 className="recipe-modal__title">{title}</h2>
        <p className="recipe-modal__description">{description}</p>
        <button className="recipe-modal__btn" onClick={closeModal}>
          Close
        </button>
      </div>
    </div>
  );
};

export default RecipeModal;
