import React, { Component } from 'react';

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
      error: null,
      registrationSuccess: false,  // New state for handling success message
    };
  }

  onInputChange = (field, value) => {
    this.setState({ [field]: value });
  };

  onSubmitRegister = () => {
    const { username, email, password, confirmPassword } = this.state;
    if (password !== confirmPassword) {
      this.setState({ error: 'Passwords do not match!' });
      return;
    }

    // Register the user (send to API)
    fetch('http://localhost:3002/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, email, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        // On successful registration, show the success message
        this.setState({
          registrationSuccess: true,
          error: null,  // Reset error state
        });
        setTimeout(() => {
          this.setState({ registrationSuccess: false });  // Hide the success message after 3 seconds
        }, 3000);
      })
      .catch((error) => {
        this.setState({ error: 'Registration failed. Please try again.' });
      });
  };

  render() {
    const { username, email, password, confirmPassword, error, registrationSuccess } = this.state;
    return (
      <div className="pa4">
        <h2 className="f2">Register</h2>

        {/* Display error message if any */}
        {error && <p className="red">{error}</p>}

        {/* Registration Success Message */}
        {registrationSuccess && (
          <div className="pa3 ba b--green bg-light-green mb3">
            You have successfully registered. You can sign in now.
          </div>
        )}

        {/* Registration Form */}
        <div className="mb3">
          <input
            type="text"
            placeholder="Username"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={username}
            onChange={(e) => this.onInputChange('username', e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={email}
            onChange={(e) => this.onInputChange('email', e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={password}
            onChange={(e) => this.onInputChange('password', e.target.value)}
          />
          <input
            type="password"
            placeholder="Confirm Password"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={confirmPassword}
            onChange={(e) => this.onInputChange('confirmPassword', e.target.value)}
          />
          <button
            onClick={this.onSubmitRegister}
            className="pa2 ba bw1 bg-light-green"
          >
            Register
          </button>
        </div>
      </div>
    );
  }
}

export default Register;
