import React from "react";
import "../../RecipeBox.css";  // Import the styles for RecipeBox

const RecipeBox = () => {
  return (
    <div className="recipe-wrapper">
      <RecipeCard
        img="https://images.unsplash.com/photo-1536304929831-ee1ca9d44906?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=400&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ"
        title="The Everyday Salad"
        description="Take your boring salads up a notch. This recipe is perfect for lunch and only contains 5 ingredients!"
      />
      <RecipeCard
        img="https://images.unsplash.com/photo-1476124369491-e7addf5db371?ixlib=rb-1.2.1&q=85&fm=jpg&crop=entropy&cs=srgb&ixid=eyJhcHBfaWQiOjE0NTg5fQ"
        title="Simple Risotto"
        description="Fear Risotto no more! This simple recipe is perfect for family dinners."
      />
      <RecipeCard
        img="https://images.unsplash.com/photo-1529928520614-7c76e2d99740?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=400&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ"
        title="Baked Cod with Vegetables"
        description="Baked Cod with Vegetables. 30-minute meal!"
      />
    </div>
  );

  function RecipeCard(props) {
    return (
      <div className="recipe-card">
        <img src={props.img} alt={props.title} className="recipe-card__image" />
        <h2 className="recipe-card__title">{props.title}</h2>
        <p className="recipe-card__description">{props.description}</p>
        <button className="recipe-card__btn">View Recipe</button>
      </div>
    );
  }
};

export default RecipeBox;
