import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import "../../RecipeModal.css";

function RecipeModal({ show, onHide, onSave, initialData = {} }) {
    const [title, setTitle] = useState('');
    const [ingredients, setIngredients] = useState('');
    const [description, setDescription] = useState('');
    const [photo, setPhoto] = useState('');

    // Reset fields to initial data when modal is opened
    useEffect(() => {
        if (show) {
            setTitle(initialData.title || '');
            setIngredients(initialData.ingredients || '');
            setDescription(initialData.description || '');
            setPhoto(initialData.photo || '');
        }
    }, [show, initialData]);

    const handleSave = (e) => {
        e.preventDefault();
        const recipeData = { title, ingredients, description, photo };
        onSave(recipeData);
        // Clear fields after saving
        setTitle('');
        setIngredients('');
        setDescription('');
        setPhoto('');
        onHide();
    };

    return (
        <Modal show={show} onHide={onHide}>
            <Modal.Header closeButton>
                <Modal.Title>{initialData.id ? 'Edit Recipe' : 'Add Recipe'}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={handleSave}>
                    <Form.Group className="w-100">
                        <Form.Label>Title</Form.Label>
                        <Form.Control 
                            type="text" 
                            value={title} 
                            onChange={(e) => setTitle(e.target.value)} 
                            required
                        />
                    </Form.Group>
                    <Form.Group className="w-100">
                        <Form.Label>Ingredients</Form.Label>
                        <Form.Control 
                            as="textarea" 
                            rows={3} 
                            value={ingredients} 
                            onChange={(e) => setIngredients(e.target.value)} 
                            required
                        />
                    </Form.Group>
                    <Form.Group className="w-100">
                        <Form.Label>Description</Form.Label>
                        <Form.Control 
                            as="textarea" 
                            rows={3} 
                            value={description} 
                            onChange={(e) => setDescription(e.target.value)} 
                            required
                        />
                    </Form.Group>
                    <Form.Group className="w-100">
                        <Form.Label>Photo URL</Form.Label>
                        <Form.Control 
                            type="text" 
                            value={photo} 
                            onChange={(e) => setPhoto(e.target.value)} 
                            required
                        />
                    </Form.Group>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={onHide}>Cancel</Button>
                        <Button variant="primary" type="submit">
                            Save Recipe
                        </Button>
                    </Modal.Footer>
                </Form>
            </Modal.Body>
        </Modal>
    );
}

export default RecipeModal;
