import React, { Component } from 'react';

class RecipeBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      recipes: [],
      name: '',
      description: '',
      ingredients: '',
      url_photo: '',
    };
  }

  componentDidMount() {
    this.fetchRecipes();
  }

  fetchRecipes = () => {
    fetch(`http://localhost:3002/recipes/${this.props.user.id}`)
      .then((response) => response.json())
      .then((recipes) => this.setState({ recipes }))
      .catch((err) => console.error('Error fetching recipes:', err));
  };

  onInputChange = (field, value) => {
    this.setState({ [field]: value });
  };

  onSubmitRecipe = () => {
    const { name, description, ingredients, url_photo } = this.state;
    const { user } = this.props;

    fetch('http://localhost:3002/recipes', {
      method: 'post',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        userId: user.id,
        name,
        description,
        ingredients,
        url_photo,
      }),
    })
      .then(() => {
        this.fetchRecipes();
        this.setState({ name: '', description: '', ingredients: '', url_photo: '' });
      })
      .catch((err) => console.error('Error adding recipe:', err));
  };

  onDeleteRecipe = (recipeId) => {
    fetch(`http://localhost:3002/recipes/${recipeId}`, { method: 'delete' })
      .then(() => this.fetchRecipes())
      .catch((err) => console.error('Error deleting recipe:', err));
  };

  render() {
    const { recipes, name, description, ingredients, url_photo } = this.state;

    return (
      <div className="pa4">
        <div className="mb3">
          <input
            type="text"
            placeholder="Recipe Name"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={name}
            onChange={(e) => this.onInputChange('name', e.target.value)}
          />
          <textarea
            placeholder="Description"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={description}
            onChange={(e) => this.onInputChange('description', e.target.value)}
          />
          <textarea
            placeholder="Ingredients (comma-separated)"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={ingredients}
            onChange={(e) => this.onInputChange('ingredients', e.target.value)}
          />
          <input
            type="text"
            placeholder="Photo URL"
            className="pa2 input-reset ba bg-transparent w-100 mb2"
            value={url_photo}
            onChange={(e) => this.onInputChange('url_photo', e.target.value)}
          />
          <button onClick={this.onSubmitRecipe} className="pa2 ba bw1 bg-light-green">
            Add Recipe
          </button>
        </div>

        <div>
          {recipes.map((recipe) => (
            <div key={recipe.id} className="ba b--black-20 pa3 mb3">
              <h2>{recipe.name}</h2>
              <p>{recipe.description}</p>
              <p>
                <strong>Ingredients:</strong> {recipe.ingredients}
              </p>
              <img src={recipe.url_photo} alt={recipe.name} className="mw-100" />
              <button
                onClick={() => this.onDeleteRecipe(recipe.id)}
                className="pa2 ba bw1 bg-light-red mt2"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default RecipeBox;
