// src/components/RecipeBox.js
import React, { useState } from 'react';
import { Button, Card, Container, Row, Col } from 'react-bootstrap';
import RecipeModal from '../RecipeModal/RecipeModal';
import "../../RecipeBox.css";

function RecipeBox() {
    const [recipes, setRecipes] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [selectedRecipe, setSelectedRecipe] = useState(null);

    const handleAdd = () => {
        setSelectedRecipe(null);
        setShowModal(true);
    };

    const handleEdit = (recipe) => {
        setSelectedRecipe(recipe);
        setShowModal(true);
    };

    const handleDelete = (id) => {
        setRecipes(recipes.filter((recipe) => recipe.id !== id));
    };

    const handleSave = (recipeData) => {
        if (selectedRecipe) {
            // Update existing recipe
            setRecipes(recipes.map((recipe) => recipe.id === selectedRecipe.id ? { ...recipe, ...recipeData } : recipe));
        } else {
            // Add new recipe
            setRecipes([...recipes, { id: Date.now(), ...recipeData }]);
        }
    };

    return (
        <Container>
            <h1 className="my-4">Recipe Box</h1>
            <Button onClick={handleAdd} className="mb-3">Add New Recipe</Button>
            <Row>
                {recipes.map((recipe) => (
                    <Col md={4} key={recipe.id}>
                        <Card className="mb-4">
                            {recipe.photo && <Card.Img variant="top" src={recipe.photo} alt={recipe.title} />}
                            <Card.Body>
                                <Card.Title>{recipe.title}</Card.Title>
                                <Card.Text><strong>Ingredients:</strong> {recipe.ingredients}</Card.Text>
                                <Card.Text><strong>Description:</strong> {recipe.description}</Card.Text>
                                <Button variant="warning" onClick={() => handleEdit(recipe)} className="me-2">Edit</Button>
                                <Button variant="danger" onClick={() => handleDelete(recipe.id)}>Delete</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>

            <RecipeModal
                show={showModal}
                onHide={() => setShowModal(false)}
                onSave={handleSave}
                initialData={selectedRecipe || {}}
            />
        </Container>
    );
}

export default RecipeBox;
